import React,{useEffect, useReducer, useState} from 'react';
import { Button, Navbar,NavDropdown,Container,Carousel,Card,Row,Table,ListGroup,Suspense,
Figure,Spinner } from 'react-bootstrap';
import './App.css';
import { Link,Route,Switch } from 'react-router-dom';
import axios from 'axios';
import AlertSong from './AlertSong.js'
import GoogleButton from './Google_login';


function App() {

  let [수량, 수량변경]= useState(["안녕","하세요","저는"]);
  var chart = [];
  let [loading,setLoading]= useState(true);
  let [차트정보,차트정보변경] = useState([]);

  useEffect(()=>{

    axios.get("https://btl831.github.io/example1.json")
    .then((result)=>{console.log(result.data); 차트정보변경(result.data); setLoading(false)})
    .catch();

  },[]); 

  if(loading) return (<>로딩중 <Spinner animation="border" variant="primary"  className="spinner"/></>);

  return (

    <div className="App">
      { 차트정보 >0 ? <Spinner animation="border" variant="primary" /> :null }


      <head>
      <link
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.1.0/dist/css/bootstrap.min.css"
        integrity="sha384-KyZXEAg3QhqLMpG8r+8fhAXLRk2vvoC2f3B09zVXn8CA5QIVfZOJ3BCsw2P0p/We"
        crossorigin="anonymous"
      />
      </head>

      {/* navbar */}
      <Navbar bg="dark" variant="dark">
        <NavDropdown title="Dropdown" id="basic-nav-dropdown">
          <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
          <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
          <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
        </NavDropdown>
        <Container>
          <Navbar.Brand href="#home">
            <img
              alt=""
              src="/logo.svg"
              width="30"
              height="30"
              className="d-inline-block align-top"
            />{' '}
          BTL 831 INTRO
          </Navbar.Brand>
          <GoogleButton />
        </Container>
      </Navbar>



      {/* 첫번째 라우트 */}
      <Route exact path = "/">
        {/* 첫번째 섹션 */}
        <body className = "body">
          <div className="container">
            <div className="row">
              <hr/>

              <Carousel className = "carousel">
                <Carousel.Item>
                  <Row className="justify-content-md-center">

                    {
                      수량.map((a,i)=>{
                        return(<Card_Jumbo 곡={차트정보[i]} i ={i}/>)
                      })
                    }

                  </Row>
                </Carousel.Item>
                {/* 2페이지 */}
                <Carousel.Item>
                  <Row className="justify-content-md-center">
                    {
                      수량.map(function(a,i){
                        return(<Card_Jumbo 곡={차트정보[i]} key ={i}/>)
                      })
                    }
                  </Row>
                </Carousel.Item>

              </Carousel>
            </div>
            <hr/>

            {/* 두번째 섹션 */}
            <div className ="row">
              <div className= "col-md-8 sub_title ">
                <hr/>
                <h6 className = "mt-1">느낌별 Playlist</h6>
                <hr/>
                <Container className="row">
                <div className = "col-md-5 box ">
                    <Recommend/>
                  </div>

                  <div className="col-md-5 box ">
                    <Recommend/>
                  </div>
                </Container>
              </div>

              <div className = "col-md-4 ">

              <h6 className = "sub_title">TOP 100</h6>
              <Chart song={차트정보}/>
              </div>
            </div>


            {/* 세번째 섹션 */}
            <div>
              <hr/>
              <h2>아직 무엇을 할지 모르는 공간</h2>

              <textarea style = {{width :"100%"}}></textarea>
              <hr/>
            </div>

            {/* 네번째 섹션 */}
            <div className= "row">
              <h3 className="sub_title">추가요청 게시판</h3>

              <div className = "col-md-4 mt-2">
              <Community></Community>

              </div>
              <div className = "col-md-8 mt-2 sub_community"> 
              <a>여기에는 상세페이지 내용이 적힐 겁니다.</a>
              </div>

            </div>

            {/* container 마무리 */}

          </div>

          <br/>

        </body>
      </Route>

      {/* <Route path = "/title" component = {Card_Jumbo}/> */}

      {/* footer */}
      <footer className= "py-4 bg-dark">
        <div className="container">
          <p className = "m-0 text-center text-white">
            Copyright &copy; Btl_831 2021
          </p>
        </div>
      </footer>


    </div>

  );
}

function Card_Jumbo(props){
  return (
    <Card style={{ width: '18rem' }} className ="j_card mt-5" >
      <Card.Img variant="top" src="/IU.jpg" className = "mt-3"/>
      <Card.Body>
        <Card.Title>{props.곡.song}</Card.Title>
        <Card.Text>
          <p>가수 :{props.곡.singer} </p>

        </Card.Text>

        <AlertSong 곡 = {props.곡} i = {props.i}/>

      </Card.Body>

    </Card>
  )
}

function Recommend(){
  return(
    <Figure className= "mt-3">
      <Figure.Image
        width={171}
        height={180}
        alt="171x180"
        src="/black_pink.jpg"
      />
      <Figure.Caption>
        오늘은 블랙핑크 노래로 하루를 시작해 봅시다.
      </Figure.Caption>
    </Figure>
  )
}

function Chart(props){
  return (
    <Table striped bordered hover variant="dark">
      <thead>
        <tr>
          <th>순위</th>
          <th>제목</th>
          <th>가수</th>
          <th>듣기</th>
        </tr>
      </thead>
      <tbody>

        {
          props.song.map((a,i)=>{
            <tr>
              <td>{a.id}</td>
              <td>aaa</td>
              <td>BTS</td>
              <td>@mdo</td>
            </tr>
          })
        }

      </tbody>
    </Table>
  )
}

function Community(){
  return (
    <ListGroup variant="flush">
    <ListGroup.Item variant="secondary">Cras justo odio</ListGroup.Item>
    <ListGroup.Item variant="secondary">Dapibus ac facilisis in</ListGroup.Item>
    <ListGroup.Item variant="secondary">Morbi leo risus</ListGroup.Item>
    <ListGroup.Item variant="secondary">Porta ac consectetur ac</ListGroup.Item>
  </ListGroup>
  )

}

export default App;
