import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Modal, Button} from 'react-bootstrap';

export default function InfoModal() {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return (
    <>
      <Button className="nextButton" variant="secondary" onClick={handleShow}>
        Go Song
      </Button>
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>여기에 곡 정보를 넣으면 되지 않을까?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={()=>{window.location.replace("https://www.youtube.com/watch?v=v7bnOxV4jAc")} }>
            Go Listen
          </Button>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
