import React, { useEffect, useReducer, useState } from 'react';
import {
  Button, Navbar, NavDropdown, Container, Carousel, Card, Row, Table, ListGroup, Suspense,
  Figure, Spinner
} from 'react-bootstrap';
import './App.css';
import { Link, Route, Switch } from 'react-router-dom';
import axios from 'axios';
import AlertSong from './AlertSong.js'
import GoogleButton from './Google_login';


function App() {

  let [수량, 수량변경] = useState(["안녕", "하세요", "저는"]);
  var chart = [];
  let [loading, setLoading] = useState(true);
  let [차트정보, 차트정보변경] = useState([]);
  var [cardpage, setCardpage] = useState([1, 2, 3]);

  useEffect(() => {

    axios.get("https://btl831.github.io/example1.json")
      .then((result) => { console.log(result.data); 차트정보변경(result.data); setLoading(false) })
      .catch();

  }, []);

  if (loading) return (<>로딩중 <Spinner animation="border" variant="primary" className="spinner" /></>);

  return (

    <div className="App">
      <head>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@5.1.0/dist/css/bootstrap.min.css"
          integrity="sha384-KyZXEAg3QhqLMpG8r+8fhAXLRk2vvoC2f3B09zVXn8CA5QIVfZOJ3BCsw2P0p/We"
          crossorigin="anonymous"
        />
      </head>

      {/* navbar */}
      <Navbar bg="dark" variant="dark">
        <NavDropdown title="Dropdown" id="basic-nav-dropdown">
          <NavDropdown.Item href="/chart">Top 100</NavDropdown.Item>
          <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
          <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
        </NavDropdown>
        <Container>
          <Link to="">
